<?phpdefined('G_IN_SYSTEM')or exit('no');// if (ini_get('display_errors')) {//     ini_set('display_errors', '0');// }System::load_app_class('admin',G_ADMIN_DIR,'no');System::load_app_fun('global',G_ADMIN_DIR);class activity extends admin {		public function __construct(){		parent::__construct();		$this->db=System::load_sys_class("model");	}	public function config(){		$db = System::load_sys_class('model');		$info = $db->GetOne("select *  from `@#_activity_config` where `id` = '1' ");					if(isset($_POST['dosubmit'])){			$is_off = intval($_POST['type']);			$this->db->Query("UPDATE `@#_activity_config` SET `is_open` = '$is_off'  where `id` = '1'");			_message("配置更新成功!");		}				$config = $info['is_open'];				include $this->tpl(ROUTE_M,'activity_config');	}	}?>